mapper.select:
SELECT COUNT(*)
FROM Ledger l
WHERE l.title_id = #{c.bookId}

    AND l.date >= #{c.startDate}


    AND l.date <= #{c.endDate}


    AND l.amount_type = #{c.amountType}


    AND l.category = #{c.category}


    AND l.payment_method = #{c.payment}

SELECT
l.ledger_id as id, l.date, l.amount, l.description, l.memo,
l.amount_type as amountType, l.spender,
l.title_id, l.member_id, l.category_id, l.payment_id
FROM Ledger l
WHERE l.title_id = #{c.bookId}

    AND l.date >= #{c.startDate}


    AND l.date <= #{c.endDate}


    AND l.amount_type = #{c.amountType}


    AND l.category = #{c.category}


    AND l.payment_method = #{c.payment}



    ORDER BY ${orderBy}

LIMIT #{limit} OFFSET #{offset}
