mapper.select:
SELECT COUNT(*)
FROM ledger l
JOIN book   b ON b.id = l.book_id
WHERE l.member_id = #{c.memberId}

    AND l.date >= #{c.startDate}


    AND l.date <= #{c.endDate}


    AND l.amount_type = #{c.amountType}


    AND l.category = #{c.category}


    AND l.payment_method = #{c.payment}

SELECT
l.id, l.date, l.amount, l.description, l.memo,
l.amount_type, l.category, l.payment_method,
l.book_id, l.member_id
FROM ledger l
WHERE l.member_id = #{c.memberId}

    AND l.date >= #{c.startDate}


    AND l.date <= #{c.endDate}


    AND l.amount_type = #{c.amountType}


    AND l.category = #{c.category}


    AND l.payment_method = #{c.payment}



    ORDER BY ${orderBy}

LIMIT #{limit} OFFSET #{offset}
